mapper.select:
select t1.id, t1.author_id, t1.author_name, t1.language, t1.is_publish, t1.avatar, t1.type, t1.country, t1.birthday, t1.identity, t1.rank, t1.create_time, t1.update_time, COUNT(t2.author_name) as total from (select * from music_authors
where language = #{category}
order by `rank` desc limit #{start},#{pageSize} )t1 LEFT JOIN music t2 on t1.author_name = t2.author_name GROUP BY t2.author_name,t1.id
